mapper.insert:
INSERT INTO dbo.MASTER
(API_KEY
, DLV_STTUS_CD
, PAGE_CNT
, PRINT_TYPE_NM
, TOTAL_SEND_CNT
, TR_KEY
, PST_FILE)
VALUES
(#{apiKey}
, #{dlvSttusCd}
, #{pageCnt}
, #{printTypeNm}
, #{totalSendCnt}
, #{trKey}
,#{pstFile})

mapper.update:
UPDATE dbo.MASTER
SET
API_KEY = #{apiKey}
, DLV_STTUS_CD = #{dlvSttusCd}
, PAGE_CNT = #{pageCnt}
, PRINT_TYPE_NM = #{printTypeNm}
, TOTAL_SEND_CNT = #{totalSendCnt}
, PST_FILE = #{pstFile}
WHERE TR_KEY = #{trKey}
